unsigned int cursorPos = MENU_G1;
static unsigned char pad;

const char text_title[] = "      SELECT GAME:";
const char text_g1[]    = "Game 1";
const char text_g2[]  	= "Game 2";
const char text_g3[]  	= "Game 3";

void load_chr_ram(const unsigned char* data, unsigned int size) {
	unsigned int i;
	
	vram_adr(0x00);

    for (i = 0; i < size; i++) {
        vram_put(data[i]);
    }
}

void put_str(unsigned int adr, const char *str) {
	vram_adr(adr);
	while(1) {
		if(!*str) break;
		vram_put((*str++)-0x20);
	}
}

void set_colors(void) {
	pal_col(0,0x0f);
	pal_col(1,0x21);
	pal_col(2,0x31);
	pal_col(3,0x11);
}

void draw_menu(void) {
	set_colors();
	put_str(NTADR_A(4, 8), text_title);
    put_str(NTADR_A(4, 10), text_g1);
    put_str(NTADR_A(4, 12), text_g2);
    put_str(NTADR_A(4, 14), text_g3);
}

void set_cursor(unsigned char sprite1, unsigned char sprite2, unsigned char sprite3) {
	unsigned char vram_buffer[10];
	unsigned short adr1 = NTADR_A(2, 10);
	unsigned short adr2 = NTADR_A(2, 12);
	unsigned short adr3 = NTADR_A(2, 14);

	vram_buffer[0] = MSB(adr1);
	vram_buffer[1] = LSB(adr1);
	vram_buffer[2] = sprite1;

	vram_buffer[3] = MSB(adr2);
	vram_buffer[4] = LSB(adr2);
	vram_buffer[5] = sprite2;

	vram_buffer[6] = MSB(adr3);
	vram_buffer[7] = LSB(adr3);
	vram_buffer[8] = sprite3;

	vram_buffer[9] = NT_UPD_EOF;

	set_vram_update(vram_buffer);
}

void update_cursor(void) {
	switch(cursorPos) {
		case MENU_G1: { set_cursor(0x1e, 0x00, 0x00); break; }
		case MENU_G2: { set_cursor(0x00, 0x1e, 0x00); break; }
		case MENU_G3: { set_cursor(0x00, 0x00, 0x1e); break; }
	};
}

void cursorPosDec() {
	if(cursorPos <= MENU_G1) return;
	cursorPos--;
}

void cursorPosInc() {
	if (cursorPos >= MENU_G3) return;
	cursorPos++;
}

void jump_to_game(unsigned char game_number) {}

void main(void) {
	ppu_off();
	load_chr_ram(CHR, sizeof(CHR));
    draw_menu();
    update_cursor();
	ppu_on_all();

    while (1) {
		
		pad = pad_trigger(0);

		if(pad & PAD_UP) {
            cursorPosDec();
		}
		if(pad & PAD_DOWN) {
            cursorPosInc();
		}
		
		update_cursor();
		ppu_wait_nmi();
		
		if(pad & PAD_START) {
			switch(cursorPos) {
				case 0: jump_to_game(1);
				case 1: jump_to_game(2);
				case 2: jump_to_game(3);
			};
		}
    }
}
